    @if($tryItOut['enabled'] ?? true)
        <script>
            var tryItOutBaseUrl = "{{ $tryItOut['base_url'] ?? $baseUrl }}";
            var useCsrf = Boolean({{ $tryItOut['use_csrf'] ?? 'false' }});
            var csrfUrl = "{{ $tryItOut['csrf_url'] ?? '' }}";
